import Button from "./button";

const image_base = 'https://bhotahiti-bucket.s3.ap-south-1.amazonaws.com';

export default function Variation({variation, image}){
    console.log(variation);
    return(
        <div className="col-12 p-0">
             <div className="row m-0">
                <div className="row m-0">
                    <section className="col-3">
                         <label className="text-secondary">Color</label>   
                    </section> 
                    <section className="col-3">
                        <img className="col-4 img-responsive p-2 rounded" src={`${image_base}${image}`}/>
                    </section>    
                </div>
                <Button content="6gb" className="border-dotted px-2 py-1"/>
             </div>   
        </div>
    )
}
