import axios from "axios";

const p_url = 'http://139.59.53.101/api/products/products/60f7f6ca8965be0019aef106';
async function fetchSingleProduct(){
    try{
        const { data } = await axios.get(p_url);
        return { data }
    }catch(e){
        return { error: e.toString() }
    }
}

export {
    fetchSingleProduct
}
