import { useEffect, useState } from "react";
import { fetchSingleProduct } from "./components/api";

function App() {
  const [ loading, setLoading ] = useState(true);
  const [ product, setProduct ] = useState({});



  const getProduct = async()=> {
    const { error, data } = await fetchSingleProduct();
    setLoading(false);
    console.log(error, data);
  }

  useEffect(()=> {
    getProduct();
    return()=> {

    }
  }, [])
  return (
     <main className="container-fluid p-0 bg-light">
       <BreadCrumb/>
       <div className="bg-light mt-3 p-2 container">
          <div className="row m-0">
            <section className="col-5 bg-white">
                  <img src="https://rukminim1.flixcart.com/image/416/416/kmax8y80/mobile/n/x/i/note-10-mzb084jlk1-redmi-original-imagf8hpm7nzzday.jpeg?q=70"/>
            </section>
            <section className="col-5">
                  <h1>Section two</h1>
            </section>
          </div>
       </div>
     </main>
  );
}


const BreadCrumb = ()=> {
  return(
    <header className="col-12 px-4 py-3 text-primary" style={{backgroundColor: '#ececec'}}>
      <a>Products  </a> 
      <span className="mx-2">{'>'}</span>
      <a className="">Product name </a> 
    </header>
  )
}

export default App;
