import { useEffect, useState } from "react";
import { fetchSingleProduct } from "./components/api";
import './style.css';
import Loader from "./components/loader";
import Preview from "./components/preview";
import Details from "./components/details";

function App() {
  const [ loading, setLoading ] = useState(true); //loading handler state 
  const [ product, setProduct ] = useState({}); //product detail state


  //requesting to api
  const getProduct = async()=> {
    const { error, data } = await fetchSingleProduct();
    setLoading(false);
    const isValidate = (!error && data);
    if(isValidate) setProduct(data)
    else {
      // can set error handler state here
      setProduct({})
    }
  }

  //initial loader
  useEffect(()=> {
    getProduct();

    return()=> {
        //clear all the states here
    }
  }, []);

  const isReady = !(loading && product);

  console.log(product);

  return (
     <main className="container-fluid p-0 bg-light">
       <BreadCrumb/>
       {isReady ? <div className="bg-light mt-3 p-2 container">
          <div className="row m-0">
            <section className="col-5 bg-white sticky-top rounded-5 p-2">
              <Preview/>
            </section>
            <section className="col-7">
                <Details/>
            </section>
          </div>
       </div> : <Loader/>}
     </main>
  );
}


const BreadCrumb = ()=> {
  return(
    <header className="col-12 px-4 py-3 text-primary sticky-top z-index" style={{backgroundColor: '#ececec'}}>
      <a>Products  </a> 
      <span className="mx-2">{'>'}</span>
      <a className="">Product name </a> 
    </header>
  )
}

export default App;
